import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Fetchdata } from './Store2'

const Home2 = () => {
    const { loading, data, error } = useSelector((state) => state)
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(Fetchdata())
    }, [])

    const getGujaratiErrorMessage = (error) => {
        if (!error) return null;

        if (error === "Network Error") {
            return "ઇન્ટરનેટ ઉપલબ્ધ નથી અથવા સર્વર પહોંચી શકાયો નથી.";
        }

        if (error.includes("404")) {
            return "માગેલી માહિતી મળી નથી (404 ભૂલ).";
        }

        if (error.includes("500")) {
            return "સર્વરમાં આંતરિક ભૂલ છે (500).";
        }

        if (error.includes("timeout")) {
            return "વિનંતી સમયસમાપ્ત થઈ ગઈ.";
        }

        return "કંઈક ખોટું થયું છે: " + error;
    }

    if (loading) {
        return <h1>લોડ થઈ રહ્યું છે...</h1>
    }

    if (error) {
        return <h2 style={{ color: 'red' }}>ભૂલ: {getGujaratiErrorMessage(error)}</h2>
    }

    return (
        <>
            <table border={1} cellPadding={7} cellSpacing={0}>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>name</th>
                        <th>email</th>
                        <th>password</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data && data.map((e) => (
                            <tr key={e.id}>
                                <td>{e.id}</td>
                                <td>{e.name}</td>
                                <td>{e.email}</td>
                                <td>{e.password}</td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
        </>
    )
}

export default Home2
