function reducer(state = abc , action){
    switch(action.type){
        case "Fetch_start":
            return {
                ...state,
                loading: true,
                error: null
            }
        case "Fetch_Success":
            return {
                ...state,
                loading: false,
                data: action.payload,
                error: null
            }
        case "Fetch_Failed":
            return {
                ...state,
                loading: false,
                error: action.payload
            }
        default:
            return state
    }
}

function Fetchdata(){
    return async (dispatch)=>{
        dispatch({ type: "Fetch_start" })
        try {
            const response = await axios.get('https://geton.skmbpk1z.a2hosted.com/get-data.php')
            dispatch({
                type: "Fetch_Success",
                payload: response.data
            })
        } catch (error) {
            dispatch({
                type: "Fetch_Failed",
                payload: error.message || "કંઈક ખોટું થયું છે"
            })
        }
    }
}
