import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Fetchdata } from './Store2'

const Home2 = () => {
    const { loading, data, error } = useSelector((state) => state)
    const dispatch = useDispatch()

    useEffect(() => {
        dispatch(Fetchdata())
    }, [])

    if (loading === true) {
        return <h1>Loading...</h1>
    }

    // ✅ Show error if present
if (error) {
    const Message = error === "Network Error" ? "Internet નથી અથવા API ઉપલબ્ધ નથી" : error;
    return <h2 style={{ color: 'red' }}>ભૂલ: {customMessage}</h2>
}


    return (
        <>
            <table border={1} cellPadding={7} cellSpacing={0}>
                <thead>
                    <tr>
                        <th>id</th>
                        <th>name</th>
                        <th>email</th>
                        <th>password</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        data && data.map((e) => (
                            <tr key={e.id}>
                                <td>{e.id}</td>
                                <td>{e.name}</td>
                                <td>{e.email}</td>
                                <td>{e.password}</td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
        </>
    )
}

export default Home2
