import { type } from "@testing-library/user-event/dist/type";
import axios from "axios";
import { applyMiddleware, createStore } from "redux";
import { thunk } from "redux-thunk";


const abc = {
    location : false,
    data : null,
    error : null
}

function reducer(state = abc , action){
    switch(action.type){
       case"Fetch_start":
           return{
            ...state,
            loading : true
           }
           break;
       case "Fetch_Success":
         return{
            ...state,
            loading: false,
            data: action.payload
         }    
         default:
            return state
            break;
    }
}

function Fetchdata(){
      return async (dispatch)=>{
        dispatch({
            type:"Fetch_start"
        })
        try {
            var response = await axios.get('https://geton.skmbpk1z.a2hosted.com/get-data.php')
            
            dispatch({
                type:"Fetch_Success",
                payload: response.data
            })
        } catch (error) {
            
            
        }
      }
}

const Store2 = createStore (reducer,applyMiddleware(thunk))

export {Store2 , Fetchdata}
